import React, {useState} from "react";

const AuthContext = React.createContext({
    token: '', 
    isLoggedIn: false,
    login: (token) => {},
    logout: () => {}
});

export const AuthContextProvider = (props) => {
    const initialToken = localStorage.getItem('token')
    const [token, setToken] = useState(initialToken)

    const userIsLoggedIn = !!token;

    const loginHandler = (token, expirationTime) => {
        setToken(token)
        localStorage.setItem('token', token)

        const remainingTime = experationTimeHelper(expirationTime)
    }
    //login helper
    const experationTimeHelper = (expirationTime) => {
        const currentTime = new Date().getTime();
        const tokenExpiration = new Date(expirationTime).getTime();

        const remainingSessionTime = tokenExpiration - currentTime;
        return remainingSessionTime;
    }

    const logoutHandler = () => {
        setToken(null)
        localStorage.removeItem('token')
    }

    const contextValue = {
        token: token,
        isLoggedIn:userIsLoggedIn,
        login: loginHandler,
        logout: logoutHandler
    }

    return <AuthContext.Provider value={contextValue}>{props.children}</AuthContext.Provider>
}

export default AuthContext
